import React, {useState} from 'react';
import styles from "./forum.module.css"
import { FaAngleDown } from 'react-icons/fa'

function ForumPosts(props) {
    const [open, openDetails] = useState(false);

    function getDetails() {
        if(this.state.open) {
            return (
                <h2>Test</h2>
            );
        }
    }

    function expand() {
        openDetails(!this.state.open);
    }


    const posts = props.postData.map((p, index) => {
        return(
            <div className={styles.displayPost}>
                <div className={styles.top}>
                    <h2 className={styles.shiftText}>{p.title}</h2>
                    <p className={styles.shiftText}>{p.publisher}</p>
                    <FaAngleDown onClick={() => p.expand()}/>
                </div>

                {p.getDetails}
            </div>
        );
    });
    return posts;
}
export default ForumPosts;
