import React, {useState} from 'react';


function SearchBar(){
  const [search, setSearch] = useState(
    {  
       word: '',
    }
  );

  function handleChange(event) {
    const { name, value } = event.target;
    if (name === "word"){
       setSearhc({title: value, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
          content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});
    }
  }
  function onSearchClick(){


  }
  function getData () {
    fetch(`http://localhost:5000/home`)
      .then(response => response.json())
      .then(data => {
        const { query } = this.state;
        const filteredData = data.filter(element => {
          return element.name.toLowerCase().includes(query.toLowerCase());
        });

        this.setState({
          data,
          filteredData
        });
      });
  };
  return (
    <div>
      <input class="word"
        type="text" 
        id="word"
        name="word"
        value={search.word}
        onChange = {handleChange}>
      </input>
    </div>
  );
}
export default SearchBar;


