import React, {useState} from 'react';
import {Link} from "react-router-dom";

function Form(props) {   
    const [post, setPost] = useState(
       {  
          title: '',
          DateEvent: new Date(),
          DatePosted: new Date(),
          time: '',
          publisher: '',
          content: '',
          keyWords: '',
          Cost: '',
          Location: '',
          image: '',
       }
    );


 function submitForm() {
    alert("Thank you for submitting a post!")
    props.handleSubmit(post);
    setPost({title: '', DateEvent: new Date(), DatePosted: new Date(), time: '', publisher: '', content: '', keyWords: '', Cost: '', Location: '', image: ''});
 }

 function handleChange(event) {
   const { name, value } = event.target;
   if (name === "title"){
      setPost({title: value, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
         content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});
   }
   if (name === "DatePosted"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: value, time: post.time, publisher: post.publisher, 
         content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});

   }
   if (name === "DateEvent"){
     setPost({title: post.title, DateEvent: value, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
      content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});
   }
   if (name === "time"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: value, publisher: post.publisher, 
         content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});
   }
   if (name === "publisher"){

      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: value, 
         content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});
   }
   if (name === "content"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
         content: value, keyWords: post.keyWords, Cost: post.Cost, Location: post.Location, image: post.image});
   }
   if (name === "keyWords"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
         content: post.content, keyWords: value, Cost: post.Cost, Location: post.Location, image: post.image});   }

   if (name === "Cost"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
         content: post.content, keyWords: post.keyWords, Cost: value, Location: post.Location, image: post.image});
   }
   if (name === "Location"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
         content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: value, image: post.image});   }
   if (name === "image"){
      setPost({title: post.title, DateEvent: post.DateEvent, DatePosted: post.DatePosted, time: post.time, publisher: post.publisher, 
         content: post.content, keyWords: post.keyWords, Cost: post.Cost, Location: post.location, image: value});   }
 }

   return (

     <div >
        <form>

           <label class="event" htmlFor="title">Name of event:</label>
           <input class="event1"
             type="text"  
             id="title" 
             name="title"
             value={post.title}
             onChange={handleChange}>
             </input>
           <br></br>

           <label class="date" htmlFor="DatePosted">Today's date:</label>
           <input class="date1"
             type="date" 
             id="DatePosted" 
             name="DatePosted"
             value={post.DatePosted}
             onChange = {handleChange}>
             </input>
           <br></br>

           <label class="eventd" htmlFor="DateEvent">Date of event:</label>
           <input class="eventd1"
             type="date" 
             id="DateEvent" 
             name="DateEvent"
             value={post.DateEvent}
             onChange = {handleChange}>
             </input>
           <br></br>

           <label class="eventt" htmlFor="time">Time of event:</label>
           <input class="eventt1"
             type="time" 
             id="time" 
             name="time"
             value={post.time}
             onChange = {handleChange}>
             </input>
           <br></br>

           <label class="poster" htmlFor="publisher">Poster:</label>
           <input class="poster1"
             type="text" 
             id="publisher"
             name="publisher"
             value={post.publisher}
             onChange = {handleChange}>
              </input>
           <br></br>

           <label class="location" htmlFor="Location">Location of event:</label>
           <input class="location1"
             type="text" 
             id="Location"
             name="Location"
             value={post.Location}
             onChange = {handleChange}>
              </input>
           <br></br>

           <label class="description" htmlFor="content">Description of event:</label>
           <textarea class="description1"
             id="content" 
             name="content" 
             rows="5" cols="50"
             value={post.content}
             onChange = {handleChange}>
             </textarea>
           <br></br>

           <label class="category" htmlFor="keyWords">What category is the event under:</label>
             <select class="category1" name="keyWords" id="keyWords" value={post.keyWords} onChange = {handleChange}>
             <option value="Select">Select</option>
             <option value="Sports">Sports</option>
             <option value="Music">Music</option>
             <option value="Community">Community</option>
             <option value="Art">Art</option>
             </select>
             <br></br>

             <label class="cost" htmlFor="Cost">About how much does the event cost:</label>
             <select class="cost1" name="Cost" id="Cost" value={post.Cost} onChange = {handleChange}>
             <option value="SelectC">Select</option>
             <option value="Free">Free</option>
             <option value="Five">About 5</option>
             <option value="Ten">About 10</option>
             <option value="Highest">More than 10</option>
             </select>
             <br></br>

            <label class="image" htmlFor="image">Insert your own image:</label>

            <input class="image1" type="file" id = "image" name = "image" value = {post.image} onChange={handleChange}/>

             <br></br>
           <Link to="/home">
              <button type="button" onClick={submitForm} >
                 Submit!
              </button>
           </Link>
         </form>

         </div>
       );

 }

 export default Form;
